package cimage_test

import (
	"encoding/base64"
	"testing"

	"github.com/spf13/afero"
	. "github.com/xlune/dojo1/kadai2/xlune/imgconv/cimage"
)

var jpegImageBase64 = ""

var flgIsAllowExt = []struct {
	input   string
	isAllow bool
}{
	{"png", true}, {"jpg", true},
	{"gif", true}, {"bmp", false},
	{"avi", false}, {"", false},
}

func TestIsAllowExt(t *testing.T) {
	for _, v := range flgIsAllowExt {
		allow := IsAllowExt(v.input)
		if allow != v.isAllow {
			t.Fatalf("%s is %t, (need %t) ", v.input, allow, v.isAllow)
		}
	}
}

func TestConvert(t *testing.T) {
	input := "./input.jpg"
	output1 := "./output.png"
	output2 := "./output.gif"
	output3 := "./output.jpg"
	b, err := base64.StdEncoding.DecodeString(jpegImageBase64)
	if err != nil {
		t.Fatalf("error: %s", err)
	}

	fs := afero.NewMemMapFs()
	f, err := fs.Create(input)
	_, err = f.Write(b)
	if err != nil {
		t.Fatalf("error: %s", err)
	}

	t.Run("imageConvert", func(t *testing.T) {
		testConvert(t, fs, input, output1)
		testConvert(t, fs, output1, output2)
		testConvert(t, fs, output2, output3)
	})
}

func testConvert(t *testing.T, fs afero.Fs, input string, output string) {
	t.Helper()
	err := Convert(fs, input, output)
	if err != nil {
		t.Fatalf("error: %s", err)
	}
}
